INSERT INTO `aphorisms` (`id`, `alias_hy`, `desc_hy`, `keywords_hy`, `title_hy`, `alias_ru`, `desc_ru`, `keywords_ru`, `title_ru`, `alias_en`, `desc_en`, `keywords_en`, `title_en`, `img`, `created_at`, `updated_at`) VALUES
(1, 'կասկածներ-ձեռքբերել', 'Մեր կասկածները ստիպում են մեզ կորցնել այն, ինչ կարող էինք ձեռք բերել, եթե վախ չզգայինք:', 'կասկածներ կորցնել ձեռք բերել վախ զգալ', 'Կասկածները', 'Вступительные сомнения', 'Наши сомнения заставляют нас терять то, что мы могли бы иметь, если бы не боялись.', 'потерять сомнения, чтобы обрести страх', 'сомнения', 'doubt-achieve', 'Our doubts make us lose what we could have if we did not fear.', 'lose doubts to gain fear', 'Doubts:', 'axPNjn23.jpg', '2020-03-20 10:34:45', '2020-03-20 11:25:40'),
(2, 'հաջողություն-ճանապարհ', 'Մեր ամենամեծ թերությունն այն է, որ մենք շատ արագ ենք թևաթափ լինում: Հաջողության հասնելու ամենաճիշտ ճանապարհը անընդհատ ևս մեկ անգամ փորձելն է:', 'հաջողություն թերություն փորձել', 'Հաջողություն', 'Успех-полосная', 'Наш самый большой недостаток в том, что мы очень быстрые. Лучший способ добиться успеха - это пробовать снова и снова.', 'удачи, пытаясь потерпеть неудачу', 'Удачи', 'success-way', 'Our biggest disadvantage is that we are very fast. The best way to succeed is to try again and again.', 'good luck trying to fail', 'Good luck', '5fGSzrTi.jpg', '2020-03-20 16:47:21', '2020-03-20 16:47:21'),
(3, 'քննադատել-ճանապարհ', 'Երբեք մի քննադատեք մարդուն, մինչև  նրա կոշիկներով  ճանապարհ չանցնեք', 'քննադատեք մարդուն կոշիկներ ճանապարհ', 'Քննադատել', 'критический путь', 'Никогда не критикуйте человека, пока вы не ходите на его месте', 'критиковать человека обувь', 'критиковать', 'criticism-way', 'Never criticize a person until you walk in his shoes', 'criticize a man shoes way', 'Criticize:', 'usGE2Is7.jpg', '2020-03-20 16:50:37', '2020-03-20 16:50:37'),
(4, 'չկա-ավելի-ռիսկային-բան', 'ռիսկային առանց ռիսկի ապրել', 'Ռիսկային բան', 'Ռիսկային բան', 'нет ничего более рискованного', 'рискованно жить без риска', 'Рискованная вещь', 'Рискованная вещь', 'there is nothing more risky', 'risky to live without risk', 'Risky thing', 'Risky thing', 'a1kySv9e.jpg', '2020-03-20 16:58:25', '2020-03-20 16:58:25'),
(5, 'մարդը-կարողացել-է-սարեր-շարժել', 'Մարդը, որ կարողացել է սարեր  շարժել, սկսել է նրանից, որ մանր քարեր է տեղից տեղ  քարշ տվել:', 'Մարդ սարեր շարժել մանր քարեր', 'Սարեր  շարժել', 'человек передвигающийся-в-горах', 'Человек, который смог сдвинуть горы, начал перетаскивать маленькие камни с места на место.', 'Люди двигают маленькие камни в горах', 'Двигать горы', 'man-was-able-to-move-mountains', 'The man who was able to move the mountains began by dragging small stones from place to place.', 'People move small stones in the mountains', 'Move mountains', 'ari3aFqy.jpg', '2020-03-20 16:59:47', '2020-03-20 16:59:47'),
(6, 'ոչ-մի-հաղթող', 'Ոչ մի հաղթող չի հավատում պատահականությունների', 'Ոչ մի հաղթող  հավատում պատահականությունների', 'Հաղթող', 'не-а-победитель', 'Ни один победитель не верит в совпадения', 'Ни один победитель не верит в совпадения', 'победитель', 'no-winners', 'No winner believes in coincidences', 'No winner believes in coincidences', 'Winner:', 'KCh8ncsF.jpg', '2020-03-20 17:01:15', '2020-03-20 17:01:15');

